import { Component } from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js/auto";
import { Bar } from "react-chartjs-2";
import "./App.css";

ChartJS.register(ArcElement, Tooltip, Legend);

class App extends Component {
  state = { ApiStatus: false, dataSets: [], dataLabels: [] };
  componentDidMount() {
    this.callApi();
  }

  callApi = async () => {
    const options = {
      method: "GET",
    };
    const response = await fetch(
      "https://datausa.io/api/data?drilldowns=Nation&measures=Population",
      options
    );
    const data = await response.json();
    const dataLabels = data.data.map((each) => each.Year);
    const dataSets = data.data.map((each) => each.Population);
    this.setState({ ApiStatus: true, dataSets, dataLabels });
  };

  getLoader = () => <div className="animate-loader"></div>;

  getBarData = () => {
    const { dataSets, dataLabels } = this.state;
    console.log(dataSets);
    return (
      <div className="bar-chart">
        <Bar
          data={{
            labels: dataLabels,
            datasets: [
              {
                label: "Users Gained ",
                data: dataSets,
                backgroundColor: [
                  "rgba(75,192,192,1)",
                  "#50AF95",
                  "#f3ba2f",
                  "#2a71d0",
                ],
                borderColor: "black",
                borderWidth: 2,
              },
            ],
          }}
          options={{
            plugins: {
              title: {
                display: true,
                text: "Users Gained between 2016-2020",
              },
              scales: {
                yAxes: [
                  {
                    ticks: {
                      beginAtZero: false,
                    },
                  },
                ],
              },
              legend: {
                display: true,
              },
            },
          }}
        />
      </div>
    );
  };

  render() {
    const { ApiStatus } = this.state;

    return (
      <div className="bar-chart-con">
        {ApiStatus === false ? this.getLoader() : this.getBarData()}
      </div>
    );
  }
}

export default App;
